import React from "react";
import styled from "styled-components";

const InforPerfil = ({
  photo,
  name,
  link,
  seguidores,
  seguidor,
  favoritos,
  sobre,
  empresa,
  endereco,
  site,
  destaque,
}) => (
  <Container>
    <BoxOne>
      <span></span>
      <div>
        <img src={photo} />
      </div>
      <h1>{name}</h1>
      <h4>{link}</h4>
      <section>
        <p>Seguidores</p>
        <h3>{seguidores}</h3>
      </section>
      <section>
        <p>Seguindo</p>
        <h3>{seguidor}</h3>
      </section>
      <section>
        <p>Favoritos</p>
        <h3>{favoritos}</h3>
      </section>
    </BoxOne>
    <BoxTwo>
      <h1>Sobre</h1>
      <h5>{sobre}</h5>
      <section>
        <img src="assets/business.svg" />
        <p>{empresa}</p>
      </section>
      <section>
        <img src="assets/place.svg" />
        <p>{endereco}</p>
      </section>
      <section>
        <img src="assets/link.svg" />
        <p>{site}</p>
      </section>
    </BoxTwo>
    <BoxThee>
      <h1>Destaque</h1>
      <section>
        <img src="assets/bookmarks.svg" />
        <p>{destaque}</p>
      </section>
    </BoxThee>
  </Container>
);

const Container = styled.div`
  background-color: #fff;
  width: 300px;
`;
const BoxOne = styled.div`
  overflow: hidden;
  box-shadow: 0px 0px 4px #a1a1a1;
  padding-bottom: 10px;
  margin: 15px 15px 5px 15px;
  border-radius: 7px;
  span {
    width: 100%;
    height: 80px;
    background: black;
    display: flex;
  }
  div {
    background: transparent;
    display: flex;
    justify-content: center;
    width: 100%;
    margin-top: -42px;
    img {
      height: 80px;
      width: 80px;
      border-radius: 100%;
      border: solid 4px #fff;
    }
  }
  h1 {
    color: #000;
    font-size: 20px;
    padding-left: 30px;
    margin-top: 0px;
  }
  h4 {
    color: gray;
    font-weight: 400;
    font-size: 14px;
    padding-left: 30px;
    margin-top: 2px;
    margin-bottom: 10px;
  }
  section {
    display: flex;
    justify-content: space-between;
    color: #000;
    font-size: 17px;
    padding-left: 30px;
    padding-right: 30px;
    margin-top: 5px;
    h3 {
      font-size: 17px;
    }
  }
`;
const BoxTwo = styled.div`
  overflow: hidden;
  box-shadow: 0px 0px 4px #a1a1a1;
  padding-bottom: 10px;
  margin: 15px 15px 5px 15px;
  border-radius: 7px;
  h1 {
    color: #000;
    font-size: 20px;
    padding-left: 30px;
    margin-top: 10px;
  }
  h5 {
    color: #000;
    font-size: 14px;
    padding-left: 30px;
    margin-top: 10px;
    margin-bottom: 10px;
    font-weight: 400;
  }
  section {
    color: #000;
    font-size: 14px;
    padding-left: 30px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    img {
      margin-right: 10px;
      display: flex;
      justify-content: center;
      align-items: center;
      width: 18px;
    }
  }
`;
const BoxThee = styled.div`
  overflow: hidden;
  box-shadow: 0px 0px 4px #a1a1a1;
  padding-bottom: 10px;
  margin: 15px 15px 5px 15px;
  border-radius: 7px;
  h1 {
    color: #000;
    font-size: 20px;
    padding-left: 30px;
    margin-top: 10px;
  }

  section {
    color: #000;
    font-size: 14px;
    padding-left: 30px;
    padding-right: 20px;
    margin-top: 10px;
    display: flex;
    flex-direction: row;
    img {
      margin-right: 10px;
      display: flex;
      justify-content: center;
      align-items: center;
    }
  }
`;
export default InforPerfil;
