import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 40vw;
  margin: 30px 20px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  div.featured-post-container {
    color: #fff;
    display: flex;
    width: 40vw;
    flex-direction: column;
    background: black;
        border-radius: 10px;
        overflow: hidden;
    
    img.image-featured-post {
      width: 100%;
      height: 33vh;
      object-fit: cover;
    }
    div.text-box {
        display: flex;
        flex-direction: column;
        padding: 25px;
        h2.text-title {
          margin: 0px 0px 20px 0px;
          color: #47cf73;
          font-size: 24px;
        }
        p.text-description {
          margin: 0px 0px 20px 0px;
          font-size: 18px;
          line-height: 26px;
          overflow: hidden;
   text-overflow: ellipsis;
   display: -webkit-box;
   line-height: 16px;     /* fallback */
   max-height: 60px;      /* fallback */
   -webkit-line-clamp: 3; /* number of lines to show */
   -webkit-box-orient: vertical;
        }
        p.text-date {
          font-size: 16px;
          color: #a9a9a9;
        }
      }
    }
  }
`;
