import React from "react";
import styled from "styled-components";

const Tags = ({ content, onClick, iconTags }) => (
  <Container onClick={() => onClick()}>
    {content}
    <img src={iconTags} />
  </Container>
);

const Container = styled.div`
  background-color: #0017ff1c;
  color: #0017ff;
  width: auto;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  height: 35px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  animation: 0.2s;
  transition: 0.2s;
  padding: 0px 10px;
  img {
    width: 20px;
    margin-left: 10px;
  }
  :hover {
    animation: 0.2s;
    transition: 0.2s;
    border: solid 2px #0017ff;
  }
`;

export default Tags;
