import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 80%;
  margin: 30px 0px;
  flex-direction: row;
  background: black;
  justify-content:: center;
  align-items: center;
  font-size: 13px;
  object-fit: cover;
  img.image-author {
    width: 50px;
    height: 50px;
    margin-right: 10px;
    border-radius: 50%;
  }
`;
