import React, { useState, useEffect } from "react";

import { Section } from "./styles.js";
import FeaturedPost from "../../components/featured-post/index.jsx";
import PostList from "../../components/post-list/index.jsx";
import AuthorBadge from "../../components/author-bagde";

export default function Home() {
  const [removeLoading, setRemoveLoading] = useState(false);

  const [posts, setPosts] = useState();
  async function getPosts() {
    const response = await fetch(
      "https://cms-blog-tutorial.herokuapp.com/api/posts?populate=*"
    );

    const data = await response.json();

    setPosts(data.data);

    setRemoveLoading(true);
  }
  useEffect(() => {
    getPosts();
  }, []);

  return (
    <Section>
      <div className="BoxTop">
        <div className="BoxText">
          <h1>Blog da GO-K</h1>
          <br />
          <p>
            Lorem ipsum hac posuere taciti iaculis curabitur ultrices primis,
            lobortis sociosqu egestas rutrum taciti leo nisl quis leo, luctus
            consequat lectus luctus risus at risus.
          </p>
        </div>
        <div className="BoxImageMouse">
          <img
            className="ImageMouseArrow"
            alt=""
            src="/assets/images/arrowBottom.png"
          />
          <img className="ImageMouse" alt="" src="/assets/images/mouse.png" />
        </div>
      </div>
      <div>
        <ContentLoader
          speed={2}
          width={400}
          height={460}
          viewBox="0 0 400 460"
          backgroundColor="#656161"
          foregroundColor="#934848"
        >
          <rect x="28" y="261" rx="2" ry="2" width="341" height="13" />
          <rect x="29" y="282" rx="2" ry="2" width="168" height="13" />
          <rect x="14" y="12" rx="2" ry="2" width="377" height="229" />
          <rect x="86" y="40" rx="0" ry="0" width="2" height="0" />
          <rect x="30" y="320" rx="0" ry="0" width="336" height="7" />
          <rect x="30" y="336" rx="0" ry="0" width="336" height="7" />
          <rect x="30" y="352" rx="0" ry="0" width="209" height="6" />
          <rect x="30" y="391" rx="0" ry="0" width="132" height="6" />
        </ContentLoader>
        {posts && posts.length > 0 && (
          <FeaturedPost post={posts[0].attributes} />
        )}
        <PostList posts={posts} />
      </div>
      {!removeLoading && <AuthorBadge />}
    </Section>
  );
}
