import React from "react";
import "./styles.css";
import profileImage from "../../assets/image/profile.jpg";
export default function AuthorBadge() {
  return (
    <div className="box-autor-badge-container">
      <div className="autor-badge-container">
        <img className="autor-badge-image" src={profileImage} />
        <div>
          <h3>Emerson Souza</h3>
          <p>Teach Lead</p>
        </div>
      </div>
    </div>
  );
}
