import styled from "styled-components";
export const Section = styled.section`
  background-color: #111;
  color: #fff;
  display: flex;
  height: calc(100vh - 70px);
  div.BoxTop{
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    margin: 0;
    padding: 0;
    background: radial-gradient(ellipse at center, rgb(0 0 0 / 0%) 0%, rgba(255, 255, 255, 0) 100%), radial-gradient(ellipse at 70% 60%, #000000 0%, rgba(195, 224, 96, 0) 30%), radial-gradient(ellipse at bottom left, #004758 0%, rgb(0 2 2 / 0%) 70%), linear-gradient(135deg, rgba(18, 46, 119, 0) 0%, rgba(18, 46, 119, 0) 75%, #3e0000 100%), linear-gradient(to right, #0e033c 0%, #6a0409 17%, #0a00b9 74%, #62025c 100%);
    background-blend-mode: screen, hue, color, overlay, normal;
  }
`;
