import React, { useState, useEffect } from "react";
import FeaturedPost from "../../../components/featured-post";
import PostList from "../../../components/post-list";

const mock = [
  {
    image:
      "https://images.ecycle.com.br/wp-content/uploads/2021/05/20195924/o-que-e-paisagem.jpg",
    date: "July 14, 2020",
    title: "Tititulo de teste",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software li      ",
  },
  {
    image: "https://static.escolakids.uol.com.br/2019/07/paisagem-natural.jpg",
    date: "July 01, 2020",
    title: "Tititulo de teste 2",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages,",
  },
  {
    image:
      "https://freesider.com.br/wp-content/uploads/2017/04/paisagens-para-conhecer-na-A%CC%81frica.jpg",
    date: "Tititulo de teste 3",
    title: "July 23, 2020",
    description:
      "Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages,",
  },
];
export default function Home() {
  const [posts, setPosts] = useState(mock);

  async function getPosts() {
    const response = await fetch(
      "https://cms-blog-tutorial.herokuapp.com/api/posts?populate"
    );
    const data = await response.json();
    setPosts(data.data);
  }

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <div>
      {posts && posts.length > 0 && <FeaturedPost post={posts[0]} />}
      <PostList posts={posts} />
    </div>
  );
}
