import React, { useState, useEffect } from "react";
import { Section } from "./styles.js";
import FeaturedPost from "../../components/featured-post/index.jsx";
import PostList from "../../components/post-list/index.jsx";
import "./animation.js";
export default function Home() {
  const [posts, setPosts] = useState();
  let v = 0;

  function draw() {
    v = 1 - v;
    document.body.classList.add("scale");
    setTimeout(() => {
      document.body.classList.remove("scale");
    }, 3000);
    document.body.classList.toggle("shift");
    document.querySelectorAll(".inner-" + v).forEach((cell) => {
      cell.style.setProperty(
        "--color-a",
        "var(--color" + Math.floor(Math.random() * 11) + ")"
      );
      cell.style.setProperty(
        "--color-b",
        "var(--color" + Math.floor(Math.random() * 11) + ")"
      );
      cell.style.setProperty(
        "--color-c",
        "var(--color" + Math.floor(Math.random() * 11) + ")"
      );
      cell.style.setProperty(
        "--clip-a",
        "var(--clip" + Math.floor(Math.random() * 8) + ")"
      );
      cell.style.setProperty(
        "--clip-b",
        "var(--clip" + Math.floor(Math.random() * 8) + ")"
      );
      cell.style.setProperty("--rotate-1", Math.floor(Math.random() * 6) * 90);
      cell.style.setProperty("--rotate-2", Math.floor(Math.random() * 6) * 90);
      cell.style.setProperty("--opacity", Math.floor(Math.random() * 4) - 2);
      cell.style.setProperty("--opacity-2", Math.floor(Math.random() * 4) - 1);
    });
    document.querySelectorAll(".cell").forEach((cell) => {
      cell.style.setProperty("--modifier", Math.floor(Math.random() * 2) + 1);
    });
  }

  draw();

  setInterval(function () {
    draw();
  }, 4500);

  async function getPosts() {
    const response = await fetch(
      "https://cms-blog-tutorial.herokuapp.com/api/posts?populate=*"
    );

    const data = await response.json();

    setPosts(data.data);
  }

  useEffect(() => {
    getPosts();
  }, []);

  return (
    <Section>
      <div className="BoxTop">
        <div className="BoxText">
          <h1>Blog da GO-K</h1>
          <br />
          <p>
            Lorem ipsum hac posuere taciti iaculis curabitur ultrices primis,
            lobortis sociosqu egestas rutrum taciti leo nisl quis leo, luctus
            consequat lectus luctus risus at risus.
          </p>
        </div>
        <div className="BoxImageMouse">
          <img
            className="ImageMouseArrow"
            alt=""
            src="/assets/images/arrowBottom.png"
          />
          <img className="ImageMouse" alt="" src="/assets/images/mouse.png" />
        </div>
      </div>
      <div>
        {posts && posts.length > 0 && (
          <FeaturedPost post={posts[0].attributes} />
        )}
        <PostList posts={posts} />
      </div>
    </Section>
  );
}
