import styled from "styled-components";

export const BtnBlack = styled.button`
  background-color: black;
  color: #fff;
  width: 85%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  height: 40px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  box-shadow: 0px 3px 7px gray;
  animation: 0.5s;
  transition: 0.5s;
  transform: scale(1);
  :hover {
    animation: 0.5s;
    transition: 0.5s;
    transform: scale(1.1);
  }
`;
const BtnTags = styled.div`
  background-color: #0017ff1c;
  color: #0017ff;
  width: auto;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  height: 35px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  animation: 0.2s;
  transition: 0.2s;
  padding: 0px 10px;
  box-sizing: border-box;
  border: solid 2px Transparent;
  img {
    width: 20px;
    margin-left: 10px;
  }
  :hover {
    animation: 0.2s;
    transition: 0.2s;
    border: solid 2px #0017ff;
  }
`;
