import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 100%;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  div.featured-post-container {
    color: #fff;
    display: flex;
    width: 80%;
    height: auto;
    margin: 30px 0px;
    justify-content: center;
    align-items: center;
    flex-direction: row;
    div.box-content {
      background: #111;
      width: 100%;
      margin-left: -300px;
      width: 400px;
      padding: 25px;
      border-radius: 10px;
      border: solid 2px #38d430;
      border-style: dashed;
      h2.text-title {
        margin: 0px 0px 20px 0px;
        color: #38d430;
        font-size: 24px;
      }
      p.text-description {
        margin: 0px 0px 20px 0px;
        font-size: 18px;
      }
      p.text-date {
        font-size: 16px;
        color: #a9a9a9;
      }
    }
    img.image-featured-post {
      height: 50vh;
      display: flex;
      width: 100%;
      object-fit: cover;
      border-radius: 10px;
    }
  }
`;
