import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 40vw;
  margin: 30px 20px;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  div.featured-post-container {
    color: #fff;
    display: flex;
    width: 40vw;
    flex-direction: column;
    background: black;
    div {
      width: 100%;
    }
    img.image-featured-post {
      width: 100%;
      height: 33vh;
      object-fit: cover;
    }
    div.text-box {
        background: #111;
        display: flex;
        flex-direction: column;
        padding: 25px;
        border-radius: 5px;
        h2.text-title {
          margin: 0px 0px 20px 0px;
          color: #47cf73;
          font-size: 24px;
        }
        p.text-description {
          margin: 0px 0px 20px 0px;
          font-size: 18px;
          line-height: 26px;
        }
        p.text-date {
          font-size: 16px;
          color: #a9a9a9;
        }
      }
    }
  }
`;
