import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 100%;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  div.featured-post-container {
    color: #fff;
    display: flex;
    width: 80%;
    height: auto;
    margin: 30px 0px;
    justify-content: center;
    align-items: center;
    flex-direction: row;
    div.box-content {
      background: #111;
    width: 100%;
    margin-left: -300px;
    width: 400px;
    padding: 25px;
    border-radius: 10px;
    border-left: 5px solid #38d430;
    border-right: 5px solid #0d3f4e;
    border-radius: 5px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background-position: 0 0,0 100%;
    background-repeat: no-repeat;
    -webkit-background-size: 100% 20px;
    -moz-background-size: 100% 20px;
    background-size: 100% 5px;
    background-image: -moz-linear-gradient(left,#3ec8ac 0%,#4e90a4 100%), -moz-linear-gradient(left,#3ec8ac 0%,#4e90a4 100%);
    background-image: -o-linear-gradient(left,#3ec8ac 0%,#4e90a4 100%), -o-linear-gradient(left,#3ec8ac 0%,#4e90a4 100%);
    background-image: linear-gradient(to right,#38d430 0%,#0e3e4c 80%), linear-gradient(to right,#38d430 0%,#043c4c 80%);
}
      h2.text-title {
        margin: 0px 0px 20px 0px;
        color: #38d430;
        font-size: 24px;
      }
      p.text-description {
        margin: 0px 0px 20px 0px;
        font-size: 18px;
      }
      p.text-date {
        font-size: 16px;
        color: #a9a9a9;
      }
    }
    img.image-featured-post {
      height: 50vh;
      display: flex;
      width: 100%;
      object-fit: cover;
      border-radius: 10px;
    }
  }
`;
