import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 100%;
  margin: 30px 0px;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  font-size: 13px;
  div {
    color: #fff;
    display: flex;
    width: 26vw;
    margin: 20px 25px;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: relative;
    svg {
      width: 100% !important;
    }
  }
`;
