import React from "react";
import styled from "styled-components";

const ButtonTags = ({ content, onClick, iconTags }) => (
  <Container onClick={() => onClick()}>
    <img src={iconTags} />
    {content}
  </Container>
);

const Container = styled.button`
  background-color: #0017ff26;
  color: #0017ff;
  width: 85%;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 16px;
  height: 40px;
  border: none;
  border-radius: 40px;
  cursor: pointer;
  animation: 0.5s;
  transition: 0.5s;
  transform: scale(1);
  :hover {
    animation: 0.5s;
    transition: 0.5s;
    transform: scale(1.1);
  }
`;

export default ButtonTags;
