import styled from "styled-components";
export const Section = styled.section`
  color: #fff;
  display: flex;
  width: 100%;
  margin: 30px 0px;
  flex-direction: row;
  background: black;
  justify-content:: center;
  align-items: center;
  font-size: 13px;
  div{
    background: red;
  }

`;
